export class ClientesComponent {
    constructor(objectHttp, clientesService) {
        this.objectHttp = objectHttp;
        this.clientesService = clientesService;
        this.urlapi = "http://localhost:8080/api/clientes";
    }
    ngOnInit() {
        this.res = this.objectHttp.get(this.urlapi);
        this.res.subscribe((datos) => {
            this.contenido = datos;
            console.log(this.contenido);
        });
    }
    postData() {
        this.objectHttp.post('http://localhost:8080/api/clientes', {
            codigoproducto: this.cedulacliente,
            ivacompra: this.direccioncliente,
            nitproveedor: this.emailcliente,
            nombreproducto: this.nombrecliente,
            preciocompra: this.telefonocliente
        }, {
            observe: "response"
        }).subscribe(response => {
            this.codigoRespuesta = response.status;
            this.res2 = response;
        });
    }
    onChange(evento) {
        this.file = evento.target.file[0];
    }
    async onUpload() {
        this.resultados = await this.clientesService.upload(this.file);
        console.log(this.resultados);
    }
}
