import { __decorate } from "tslib";
import { Component } from '@angular/core';
let ProductoComponent = class ProductoComponent {
    constructor(objectHttp, productoService, _config) {
        this.objectHttp = objectHttp;
        this.productoService = productoService;
        this._config = _config;
        this.urlapi = "http://localhost:8080/api/productos";
        this.recibido = false;
        _config.closeOthers = true;
    }
    //obtine info del back
    ngOnInit() {
        this.res = this.objectHttp.get(this.urlapi);
        this.res.subscribe((datos) => {
            this.contenido = datos;
            console.log(this.contenido);
        });
    }
    //envio info csv
    postData() {
        this.objectHttp.post('http://localhost:8080/api/productos', {
            codigoproducto: this.codigoproducto,
            nombreproducto: this.nombreproducto,
            nitproveedor: this.nitproveedor,
            ivacompra: this.ivacompra,
            precioventa: this.precioventa
        }, {
            observe: "response"
        }).subscribe(response => {
            this.codigoRespuesta = response.status;
            this.res2 = response;
        });
    }
    onChange(event) {
        this.file = event.target.file[0];
    }
    async onUpload() {
        console.log(this.file);
        this.resultados = await this.productoService.upload(this.file);
        console.log(this.resultados);
    }
};
ProductoComponent = __decorate([
    Component({
        selector: 'app-producto',
        templateUrl: './producto.component.html',
        styleUrls: ['./producto.component.scss']
    })
], ProductoComponent);
export { ProductoComponent };
